<template>
    <div class="daily">
        <div
            class="verse"
            @click="toDetail(verse, poetry.author)"
            @touchstart="switchCardStart"
            @touchend="switchCardEnd(1, $event)"
            :animation="animationData1"
            :style="{width: cardWidth1 + 'px', zIndex: zIndex1, top: top1 + 'vh', marginLeft: marginLeft1 + 'px'}"
        >
            <div class="author">{{poetry.author}}</div>
            <div class="chinese-calendar">
                <span>{{chineseDate}}</span>
                <span class="stamp">日赏</span>
            </div>
            <div class="poetry-content">{{verse}}</div>
        </div>
        <div
            class="verse"
            @click="toDetail(secondVerse, secondPoetry.author)"
            @touchstart="switchCardStart"
            @touchend="switchCardEnd(2, $event)"
            :animation="animationData2"
            :style="{zIndex: zIndex2, top: top2 + 'vh', width: cardWidth2 + 'px', marginLeft: marginLeft2 + 'px'}"
        >
            <div class="author">{{secondPoetry.author}}</div>
            <div class="chinese-calendar">
                <span>{{chineseDate}}</span>
                <span class="stamp">日赏</span>
            </div>
            <div class="poetry-content">{{secondVerse}}</div>
        </div>
        <div
            class="verse"
            :style="{zIndex: zIndex3, top: top3 + 'vh', width: cardWidth3 + 'px', marginLeft: marginLeft3 + 'px'}"
        ></div>
    </div>
</template>

<script>
const jinrishici = require("../../utils/jinrishici.js");
import { GetLunarDay } from "../../utils/chineseCalender.js";
let touchDotX = 0; //X按下时坐标
let touchDotY = 0; //y按下时坐标
export default {
  data() {
    return {
      poetry: {},
      verse: "",
      secondPoetry: {},
      secondVerse: "",
      chineseDate: "",
      animationData1: {},
      animationData2: {},
      top1: 20,
      top2: 19,
      top3: 18,
      cardWidth1: 280,
      cardWidth2: 270,
      cardWidth3: 260,
      zIndex1: 3,
      zIndex2: 2,
      zIndex3: 1,
      marginLeft1: -140,
      marginLeft2: -136,
      marginLeft3: -132
    };
  },

  // 所有页面的created函数会在项目加载的时候被一起调用，而且进入页面的时候不会再被调用，
  // 所以这个函数基本就不能使用了，用小程序的onLoad钩子代替吧

  // mounted：你如果从页面B返回页面A，页面A的mounted钩子不会被触发，因为页面没有被重新加载，
  // 如果有需要每次页面展示都要调用的逻辑，使用小程序的onShow代替吧

  onLoad() {
    this.getDailypoetry();
    this.getChineseCalender();
    this.getDailypoetryTwo();
  },

  onShow() {
    this.animationData1 = {};
    this.animationData2 = {};
  },

  onPullDownRefresh() {
    this.getDailypoetry().then(res => {
      wx.stopPullDownRefresh();
    });
  },

  mouted() {},

  methods: {
    getDailypoetry() {
      return new Promise((resolve, reject) => {
        jinrishici.load(result => {
          if (result.status === "success") {
            // 下面是处理逻辑示例
            console.log(result);
            this.poetry = result.data.origin;
            this.verse = result.data.content;
            resolve(result);
          }
        });
      });
    },

    getDailypoetryTwo() {
      return new Promise((resolve, reject) => {
        jinrishici.load(result => {
          if (result.status === "success") {
            // 下面是处理逻辑示例
            console.log(result);
            this.secondPoetry = result.data.origin;
            this.secondVerse = result.data.content;
            resolve(result);
          }
        });
      });
    },

    toDetail(verse, author) {
      wx.navigateTo({
        url: `/pages/poetryDetail/main?verse=${
          verse.substr(0, 7)
        }&author=${author}`
        // url: `/pages/poetryDetail/main?id=${'5c77c67d303f390037ba42be'}`
      });
    },

    /*
        *切换卡片
        */
    switchCardStart(event) {
      touchDotX = event.touches[0].pageX; // 获取触摸时的原点
      touchDotY = event.touches[0].pageY;
      console.log("起始点的坐标X:" + touchDotX);
      console.log("起始点的坐标Y:" + touchDotY);
    },

    // 移动结束处理动画
    switchCardEnd(cardNo, event) {
      // 手指离开屏幕时记录的坐标
      let touchMoveX = event.mp.changedTouches[0].pageX;
      let touchMoveY = event.mp.changedTouches[0].pageY;
      // 起始点的坐标(x0,y0)和手指离开时的坐标(x1,y1)之差
      let tmX = touchMoveX - touchDotX;
      let tmY = touchMoveY - touchDotY;
      // 两点横纵坐标差的绝对值
      let absX = Math.abs(tmX);
      let absY = Math.abs(tmY);
      //起始点的坐标(x0,y0)和手指离开时的坐标(x1,y1)之间的距离
      let delta = Math.sqrt(absX * absX + absY * absY);
      console.log("起始点和离开点距离:" + delta + "px");
      // 如果delta超过60px（可以视情况自己微调）,判定为手势触发
      if (delta >= 60) {
        // 如果 |x0-x1|>|y0-y1|,即absX>abxY,判定为左右滑动
        if (absX > absY) {
          // 如更tmX<0，即(离开点的X)-(起始点X)小于0 ，判定为左滑
          if (tmX < 0) {
            console.log("左滑=====");
            // 执行左滑动画
            this.Animation(-500, cardNo);
            // 如更tmX>0，即(离开点的X)-(起始点X)大于0 ，判定为右滑
          } else {
            console.log("右滑=====");
            // 执行右滑动画
            this.Animation(500, cardNo);
          }
          // 如果 |x0-x1|<|y0-y1|,即absX<abxY,判定为上下滑动
        } else {
          // 如更tmY<0，即(离开点的Y)-(起始点Y)小于0 ，判定为上滑
          //   if (tmY < 0) {
          //     console.log("上滑动=====");
          //     this.setData({
          //       isFront1: !this.data.isFront1
          //     });
          //     // 如更tmY>0，即(离开点的Y)-(起始点Y)大于0 ，判定为下滑
          //   } else {
          //     console.log("下滑动=====");
          //     this.setData({
          //       isFront1: !this.data.isFront1
          //     });
          //   }
          console.log("上下滑动");
        }
      } else {
        console.log("手势未触发=====");
      }
    },

    /**
     * 卡片:
     * 左滑动右滑动动画
     */
    Animation(translateXX, cardNo) {
      this.animationData1 = {};
      this.animationData2 = {};
      let animation = wx.createAnimation({
        duration: 680,
        timingFunction: "ease"
      });
      this.animation = animation;

      if (translateXX > 0) {
        this.animation
          .translateY(0)
          .rotate(20)
          .translateX(translateXX)
          .opacity(0)
          .step();
      } else {
        this.animation
          .translateY(0)
          .rotate(-20)
          .translateX(translateXX)
          .opacity(0)
          .step();
      }

      this.animation
        .translateY(0)
        .translateX(0)
        .opacity(1)
        .rotate(0)
        .step({
          duration: 10
        });

      if (cardNo === 1) {
        this.animationData1 = this.animation.export();
        this.getDailypoetry();
        setTimeout(() => {
          this.top1 = 19;
          this.cardWidth1 = 270;
          this.zIndex1 = 2;
          this.marginLeft1 = -136;

            this.top2 = 20;
            this.cardWidth2 = 280;
            this.zIndex2 = 3;
            this.marginLeft2 = -140;
        }, 500);
      } else if (cardNo === 2) {
        this.animationData2 = this.animation.export();
        this.getDailypoetryTwo();
        setTimeout(() => {
          this.top1 = 20;
          this.cardWidth1 = 280;
          this.zIndex1 = 3;
          this.marginLeft1 = -140;

          this.top2 = 19;
          this.cardWidth2 = 270;
          this.zIndex2 = 2;
          this.marginLeft2 = -136;
        }, 500);
      }
    },

    getChineseCalender() {
      var D = new Date();
      var yy = D.getFullYear();
      var mm = D.getMonth() + 1;
      var dd = D.getDate();
      var ww = D.getDay();
      var ss = parseInt(D.getTime() / 1000);
      if (yy < 100) yy = "19" + yy;
      this.chineseDate = GetLunarDay(yy, mm, dd);
    }
  }
};
</script>

