  .verse {
    font-size: 24px;
    // width: 280px;
    height: 400px;
    padding: 20px;
    background-color: #ffffff;
    position: absolute;
    left: 50%;
    font-family: @ZoomlaXingtiJ3;
    writing-mode: tb-rl;
    box-shadow: 6px 6px 4px #6e6e6e56;
    border: 2px solid #000000;
    box-sizing: border-box;
    z-index: 3;
    .author {
      position: absolute;
      bottom: 40px;
      left: 60px;
    }

    .chinese-calendar {
      position: absolute;
      left: 10px;
      top: 0;
      height: 100%;
      padding: 20px 10px 30px 0;
      border-right: 1px solid #000000;
      box-sizing: border-box;
      display: flex;
      justify-content: space-between;
      .stamp {
        height: 43px;
        display: inline-block;
        background-image: url("data:image/png;base64,");
        background-repeat: no-repeat;
        background-size: 100% 100%;
        color: rgb(241, 2, 2);
        padding: 3px;
        padding-bottom: 4px;
        text-align: center;
        font-size: 18px;
      }
    }

    .poetry-content {
      position: absolute;
